import React from 'react';
import './Fieldset.scss';

class Fieldset extends React.Component {
  constructor(props) {
    super (props);
    this.state = {value: ''}
  }


  inputChange = (event) => this.setState ({value: event.target.value});
  render () {
    return (
        <fieldset className="app-exchanger">
            <legend className="legend">Enter currency in {this.props.legend}</legend>
            <label for={this.props.currency}>{this.props.currency}</label>
            <input id={this.props.currency} type="number" placeholder={this.props.currency} value={this.state.value}></input>
        </fieldset>
    );
  }
}


export {Fieldset};
