import React from 'react';
import './Fieldset.scss';


class Fieldset extends React.Component {
  constructor(props) {
    super (props);
  }

  onChangeInUSD(value) {
    this.setState({currency: 'USD', value});
  }

  onChangeInBYN(value) {
    this.setState({currency: 'BYN', value});
  }

  inputChange = (event) => this.props.value ({value: event.target.value});
  render () {
    const value = this.props.value;
    const currency = this.props.currency;
    return (
        <fieldset className="app-exchanger">
            <legend className="legend">Enter currency in {this.props.legend}</legend>
            <label for={currency}>{currency}</label>
            <input id={currency} type="number" placeholder={currency} value={value} onChange={this.inputChange}></input>
        </fieldset>
    );
  }
}


export {Fieldset};
