import React from 'react';
import './App.scss';
import {Fieldset} from "./Fieldset";

function toBYN (USD) {
  return USD * 2.6;
}

function toUSD (BYN) {
  return BYN / 2.6;
}


class App extends React.Component {
  constructor(props) {
    super (props);
    this.state = {value: 0, currency: 'USD'}
  }

  onChangeInUSD = (value) => {this.setState({currency: 'USD', value});}

  onChangeInBYN = (value) => {this.setState({currency: 'BYN', value});}

  convertCurrency = (value, convertor) => {
    let converted = (convertor(value) * 100) / 100;
    return converted
  }

  render () {
    const value = this.state.value;
    const currency = this.state.currency;
    const USD = currency === 'USD' ? convertCurrency (value, toBYN) : value;
    const BYN = currency === 'BYN' ? convertCurrency (value, toUSD) : value;
    return (
    <div className="app">
      <h2>Live currency converter</h2>
      < Fieldset currency="USD" legend="$, USD" onChange = {this.onChangeInUSD}/>
      < Fieldset currency="BYN" legend="BYN" onChange = {this.onChangeInBYN}/>
    </div>
    )
  };
}

export default App;
