import './App.scss';
import {Fieldset} from "./Fieldset";


class App extends React.Component {
  constructor(props) {
    super (props);
    this.state = {value: 0, currency: 'USD'}
  }

  onChangeInUSD(value) {
    this.setState({currency: 'USD', value});
  }

  onChangeInBYN(value) {
    this.setState({currency: 'BYN', value});
  }

  render () {
    const value = this.state.value;
    const currency = this.state.currency;
    const USD = currency === 'USD' ? сonvertCurrency(value, toBYN) : value;
    const BYN = currency === 'BYN' ? сonvertCurrency(value, toUSD) : value;
    return (
    <div className="app">
      <h2>Live currency converter</h2>
      < Fieldset currency="USD" legend="$, USD" onChange = {this.onChangeInUSD}/>
      < Fieldset currency="BYN" legend="BYN" onChange = {this.onChangeInBYN}/>
    </div>
    )
  };
}

export default App;
